import React from 'react'
import { 
    Page, 
    Text, 
    View, 
    Document, 
    StyleSheet, 
    PDFViewer, 
    Font 
} from '@react-pdf/renderer';

import { 
    Table, 
    TableHeader, 
    TableCell, 
    TableBody, 
    DataTableCell
} from '@david.kucsai/react-pdf-table'

import { useSelector } from 'react-redux';

// Create styles
const styles = StyleSheet.create({
    page: {
        backgroundColor: '#E4E4E4',
        fontFamily: 'Sarabun',
        paddingTop: 35,
        paddingBottom: 65,
        paddingHorizontal: 35
    },
    title:{
        fontSize: 20,
        textAlign: 'center',
        marginBottom: 20
    }
});

Font.register({
    family: 'Sarabun',
    fonts:[
        {
            src: './fonts/Sarabun-Regular.ttf'
        }
    ]
})

const PdfReport = () => {

    const cartLists = useSelector((state) => state.cartReducer.cartlist)

    return (
        <PDFViewer className="container-fluid" height={600}>
            <Document>
                <Page size="A4" style={styles.page}>
                    <View>
                        <Text>Shopping cart lists</Text>
                    </View>
                    <Table
                        data={[
                            {firstName: "John", lastName: "Smith", dob: new Date(2000, 1, 1), country: "Australia", phoneNumber: "xxx-0000-0000"}
                        ]}
                    >
                        <TableHeader>
                            <TableCell>
                                First Name
                            </TableCell>
                            <TableCell>
                                Last Name
                            </TableCell>
                            <TableCell>
                                DOB
                            </TableCell>
                            <TableCell>
                                Country
                            </TableCell>
                            <TableCell>
                                Phone Number
                            </TableCell>
                        </TableHeader>
                        <TableBody>
                            <DataTableCell getContent={(r) => r.firstName}/>
                            <DataTableCell getContent={(r) => r.lastName}/>
                            <DataTableCell getContent={(r) => r.dob.toLocaleString()}/>
                            <DataTableCell getContent={(r) => r.country}/>
                            <DataTableCell getContent={(r) => r.phoneNumber}/>
                        </TableBody>
                    </Table>
                </Page>
            </Document>
        </PDFViewer>
    )
}

export default PdfReport
