import React from 'react'

const Sidebar = () => {

    // let fullName = 'Manee'

    const [fullName, setFullname] = React.useState('Manee')

    const changeName = () => {
        fullName = 'Manop'
    }

    return (
        <>
            <h3>Sidebar</h3>
            <p>Hi my name's {fullName}.</p>
            <button onClick={changeName}>Change Name</button>
        </>
    )
}

export default Sidebar
