import React from 'react'
import { Form, Container, Row, Col} from 'react-bootstrap'
import { useForm } from 'react-hook-form'
import axios from 'axios'
import { useHistory } from 'react-router-dom'

const SUPPORTED_IMAGE_FORMATS = ['image/jpeg', 'image/jpg']

const UploadPage = () => {

    const history = useHistory()

    const { handleSubmit, formState:{ errors }, register } = useForm()

    console.log(errors)

    const onSubmit = (data) => {
        console.log("upload")
    }

    return (
        <Container className="mt-4">
            <Row>
                <Col md={6}>
                    <h1>Upload Image</h1>
                    <Form onSubmit={handleSubmit(onSubmit)}>
                        <div className="mb-3 mt-4">
                            <Form.File>
                                <Form.File.Label>Choose file picture.</Form.File.Label>
                                <Form.File.Input
                                    type="file"
                                    name="picture"
                                    ref={register({
                                        required: '** Please input file image.',
                                        validate: {
                                            checkFileType: (value) => {
                                                return value && SUPPORTED_IMAGE_FORMATS.includes(value[0].type)
                                            }
                                        }
                                    })}
                                    isInvalid={errors.picture}
                                    id="upload-image"
                                />
                                {
                                    errors.picture && errors.picture.type === "required" (
                                        <Form.Control.Feedback type="invalid">
                                            {errors.picture.message}
                                        </Form.Control.Feedback>
                                    )
                                }
                                {
                                    errors.picture && errors.picture.type === "checkFileType" (
                                        <Form.Control.Feedback type="invalid">
                                            Picture supported only file type .jpg or jpeg.
                                        </Form.Control.Feedback>
                                    )
                                }
                            </Form.File>
                        </div>
                        <button className="btn btn-primary" type="submit">Upload...</button>
                    </Form>
                </Col>
            </Row><hr/>
        </Container>
    )
}

export default UploadPage
