import React from 'react'
import { Table, Image, Badge, Spinner } from 'react-bootstrap'
import axios from 'axios'
import { format } from 'date-fns'
import { th } from 'date-fns/locale'

const ProductPage = () => {

    const [product, setProduct] = React.useState([])
    const [loading, setLoading] = React.useState(false)
    const [error, setError] = React.useState(null)

    const getData = async () => {
        try {
            setLoading(true)
            const res = await axios.get('https://api.codingthailand.com/api/course')
            setProduct(res.data.data)
        } catch (error){
            console.log(error)
            setError(error)
        } finally {
            setLoading(false)
        }
    }

    React.useEffect(() => {
        getData()
    },[])

    if(loading === true){
        return(
            <div className="text-center mt-5">
                <Spinner animation="border" />
            </div>
        )
    }

    if (error) {
        return (
            <div className="text-center mt-5">
                <p>Have a problem from server,please try again.</p>
            </div>
        )
    }

    return (
        <div className="container">
            <div className="row mt-5">
                <div className="col-md-12">
                    <h2>Products</h2>
                    <Table striped bordered hover>
                        <thead>
                            <tr>
                                <th>ID</th>
                                <th>Course name</th>
                                <th>Course detail</th>
                                <th>Date create</th>
                                <th>Views</th>
                                <th>Picture</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                                product.map((p,index) => {
                                    return (
                                        <tr key={p.id}>
                                            <td>{p.id}</td>
                                            <td>{p.title}</td>
                                            <td>{p.detail}</td>
                                            <td>
                                                {
                                                    format(new Date(p.date), 'dd/MMM/yyyy', {locale: th})
                                                }
                                            </td>
                                            <td>
                                                <Badge variant="secondary">{p.view}</Badge>
                                            </td>
                                            <td>
                                                <Image src={p.picture} rounded alt={p.title} width={100} />
                                            </td>
                                        </tr>
                                    )
                                })
                            }
                        </tbody>
                    </Table>
                </div>
            </div>
        </div>
    )
}

export default ProductPage
