import React from 'react'
import { useParams } from "react-router-dom";
import axios from 'axios'
import { CardDeck, Card, Spinner } from 'react-bootstrap'

const DetailPage = () => {

    const {id, title} = useParams()
    const [detail, setDetail] = React.useState([])
    const [loading, setLoading] = React.useState(false)
    const [error, setError] = React.useState(null)
    const cancelToken = React.useRef(null)

    const getData = async () => {
        try {
            setLoading(true)
            const res = await axios.get('https://api.codingthailand.com/api/course/' + id ,{
                cancelToken: cancelToken.current.token  
            })
            setDetail(res.data.data)
        } catch (error){
            // console.log(error)
            setError(error)
        } finally {
            setLoading(false)
        }
    }

    React.useEffect(() => {

        cancelToken.current = axios.CancelToken.source()
        getData()

        return () =>  {
            // console.log('exit')
            cancelToken.current.cancel()
        }

    },[id])

    if(loading === true){
        return(
            <div className="text-center mt-5">
                <Spinner animation="border" />
            </div>
        )
    }

    if (error) {
        return (
            <div className="text-center mt-5">
                <p>Have a problem from server,please try again.</p>
                <p>{error.response.data.message}</p>
            </div>
        )
    }

    return (
        <div className="container">
            <div className="row mt-5">
                <div className="col-md-12">
                    <div className="row">
                        <h2>{title} - {id}</h2>
                    </div>
                    <div className="row">
                        <CardDeck>
                            {
                                detail.length > 0 ? (
                                    detail.map((d, index) => {
                                        return (
                                            <div className="col-md-4" key={d.ch_id}>
                                                <Card>
                                                    <Card.Body>
                                                        <Card.Title>{d.ch_title}</Card.Title>
                                                        {/* <Card.Text>
                                                            This is a wider card with supporting text below as a natural lead-in to
                                                            additional content. This content is a little bit longer.
                                                        </Card.Text> */}
                                                    </Card.Body>
                                                    {/* <Card.Footer>
                                                        <small className="text-muted">Last updated 3 mins ago</small>
                                                    </Card.Footer> */}
                                                </Card>
                                            </div>
                                        )
                                    })
                                ) : (
                                    <p>Not found data ...</p>
                                )
                            }
                        </CardDeck>
                    </div>
                </div>
            </div>
        </div>
    )
}

export default DetailPage
