import React from "react"
import { Navbar, Nav, NavDropdown } from 'react-bootstrap'
import { NavLink, useHistory } from 'react-router-dom'

const NavBar = () => {

  const history = useHistory()
  const [profile, setProfile] = React.useState(null)

  const getProfile = () => {

    const profileVal = JSON.parse(localStorage.getItem('profile')) 

    if(profileVal){
      setProfile(profileVal)
    }

  }

  React.useEffect(() => {
    getProfile()
  },[])

  return (
    <>
      <Navbar bg="warning" expand="lg">
        <Navbar>
          <NavLink className='navbar-brand' to='/' exact>
            <img
                alt="WebTestLogo"
                src="/logo192.png"
                width="30"
                height="30"
                className="d-inline-block align-top"
              />{' '}
              Web Test
          </NavLink>
          {/* <Navbar.Brand href="#home">
            <img
              alt="WebTestLogo"
              src="/logo192.png"
              width="30"
              height="30"
              className="d-inline-block align-top"
            />{' '}
            Web Test
          </Navbar.Brand> */}
        </Navbar>
        <Navbar.Toggle aria-controls="basic-navbar-nav" />
        <Navbar.Collapse id="basic-navbar-nav">
          <Nav className="mr-auto">
            <NavLink className='nav-link' to='/' activeClassName='active' exact>
              Home
            </NavLink>

            <NavLink className='nav-link' to='/product' activeClassName='active'>
              Products
            </NavLink>
            
            <NavLink className='nav-link' to='/about' activeClassName='active'>
              About
            </NavLink>

            <NavDropdown title="Workshop (Pagination + CRUD)" id="basic-nav-dropdown">

              <NavDropdown.Item 
                onClick={() => {
                  history.replace('/hospital')
                }}
              >
                Hospital Detail (Pagination)
              </NavDropdown.Item>

              <NavDropdown.Divider />

              <NavDropdown.Item 
                onClick={() => {
                  history.replace('/category')
                }}
              >
                News Category (CRUD)
              </NavDropdown.Item>

            </NavDropdown>

            <NavLink className='nav-link' to='/upload' activeClassName='active'>
              Upload
            </NavLink>
          </Nav>

          {
            profile ? (
              <span className="navbar-text text-white">
                Welcome : {profile.name}
                <button className="ml-2" variant="outline-danger">
                  Log Out
                </button>
              </span>
            ) : (
              <Nav>
                <NavLink className='nav-link' to='/register' activeClassName='active'>
                  Register
                </NavLink>

                <NavLink className='nav-link' to='/login' activeClassName='active'>
                  Log In
                </NavLink>
              </Nav>
            )
          }

        </Navbar.Collapse>
      </Navbar>
    </>
  );
};

export default NavBar;
